import Vue from 'vue';
import Router from 'vue-router';
import IndexVue from './views/index';
import HellowVue from './views/hello';
import NftnELGridVue from './views/element-ui/grid';
import NftnELFormVue from './views/element-ui/form';

Vue.use(Router);

export default new Router({
    mode: 'history',
    routes: [
        {
            path: '/',
            name: 'home',
            component: IndexVue
        },
        {
            path: '/hello',
            name: 'hello',
            component: HellowVue
        },
        {
            path: '/element-ui/grid',
            name: 'element-ui.grid',
            component: NftnELGridVue
        },
        {
            path: '/element-ui/form',
            name: 'element-ui.form',
            component: NftnELFormVue
        }
    ]
});
